static bool key_pressed = false;

bool process_record_user(uint16_t keycode, keyrecord_t *record) {
    if (record->event.pressed) key_pressed = true;
    return true;
}

bool oled_task_user(void) {
    const uint8_t  IDLE = 0, POST_TAP = 1, COOLDOWN = 2;
    static uint8_t next = IDLE;

    static uint32_t tp                 = 0;
    static uint8_t  current_tap_frame  = 0;
    static uint8_t  current_idle_frame = 0;

    if (key_pressed) {
        oled_write_raw_P(tap[current_tap_frame], ANIM_SIZE);
        current_tap_frame ^= 1;
        key_pressed = false;
        next        = POST_TAP;
    } else if (next == POST_TAP) {
        oled_write_raw_P(prep[0], ANIM_SIZE);
        next = COOLDOWN;
        tp   = timer_read32();
    } else if (next == IDLE) {
        oled_write_raw_P(idle[current_idle_frame++], ANIM_SIZE);
        if (current_idle_frame >= 3) current_idle_frame = 0;
        tp = timer_read32();
    } else if (next == COOLDOWN && timer_elapsed32(tp) > 200) {
        next = IDLE;
    }

    return true;
}
